import React, { useState, useEffect } from 'react';
import Logo from '../assets/logo.png';
import './css/Navbar.css';
import { Link } from 'react-router-dom';

function Navbar() {
    const [menuOpen, setMenuOpen] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const header = document.querySelector(".App-header");
            if (header) {
                header.style.backgroundColor = window.scrollY > 0 ? "rgba(50, 50, 55, 0.5)" : "transparent";
                header.style.backdropFilter = window.scrollY > 0 ? "saturate(180%) blur(10px)" : "none";
                header.style.WebkitBackdropFilter = window.scrollY > 0 ? "blur(10px)" : "none";
            }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    const toggleMenu = () => setMenuOpen(!menuOpen);

    return (
        <div className="App-header">
            <div className="App-logo">
                <Link to="/" smooth={true} duration={500} offset={-150}>
                    <img src={Logo} alt="Logo" width={200} />
                </Link>
            </div>
            <div className={`App-menu ${menuOpen ? "open" : ""}`}>
                <ul>
                    <li><Link to="/agenda" smooth={true} duration={500} offset={-150}>Agenda</Link></li>
                    <li><Link to='/campus-ambassador' smooth={true} duration={500} offset={-150}>CA</Link></li>
                    <li><Link to="/guidelines" smooth={true} duration={500} offset={-150}>Guidelines</Link></li>
                    <li><Link to="/domains" smooth={true} duration={500} offset={-150}>Problem Statements</Link></li>
                    <li><Link to="/contact">Contact</Link></li>
                </ul>
            </div>
            <div className="App-Register">
                <button onClick={() => setMenuOpen(false)} className="animated-button">
                    <svg xmlns="http://www.w3.org/2000/svg" className="arr-2" viewBox="0 0 24 24">
                        <path
                            d="M16.1716 10.9999L10.8076 5.63589L12.2218 4.22168L20 11.9999L12.2218 19.778L10.8076 18.3638L16.1716 12.9999H4V10.9999H16.1716Z"
                        ></path>
                    </svg>
                    <span className="text">R E G I S T E R</span>
                    <svg xmlns="http://www.w3.org/2000/svg" className="arr-1" viewBox="0 0 24 24">
                        <path
                            d="M16.1716 10.9999L10.8076 5.63589L12.2218 4.22168L20 11.9999L12.2218 19.778L10.8076 18.3638L16.1716 12.9999H4V10.9999H16.1716Z"
                        ></path>
                    </svg>
                </button>
            </div>
            <div className="App-hamburger" onClick={toggleMenu}>
                <span />
                <span />
                <span />
            </div>
        </div>
    );
}

export default Navbar;
